import React from 'react';
import config from "../config/auth.config";

const DiscordButton = () => {
    // Discord OAuth URL for user login
    const discordAuthUrl = `https://discord.com/login?client_id=${config.DISCORD_CLIENT_ID}&redirect_uri=${config.DISCORD_REDIRECT_URI}&response_type=code&scope=identify`;

    return (
        <a href={discordAuthUrl}>
            <button className="btn btn-secondary btn-lg">
                Connect with Discord
            </button>
        </a>
    );
};

export default DiscordButton;
