import React from 'react';

const DiscordButton = () => {
    // URL d'autorisation OAuth de Discord avec une URL générique
    const discordAuthUrl = `https://discord.com/api/oauth2/authorize?redirect_uri=${encodeURIComponent('http:localhost:3000/api/auth/discord-callback')}&response_type=code&scope=${encodeURIComponent('identify')}`;

    return (
        <a href={discordAuthUrl}>
            <button className="btn btn-secondary btn-lg">
                Connect avec Discord
            </button>
        </a>
    );
};

export default DiscordButton;
