import React from 'react';

const DiscordButton = () => {
    // Remplacez ces valeurs par vos propres informations d'application Discord
    const clientId = 'VOTRE_ID_CLIENT_DISCORD';
    const redirectUri = 'VOTRE_URL_DE_RETOUR_DISCORD';
    const scope = 'identify'; 

    // Construire l'URL d'autorisation OAuth de Discord
    const discordAuthUrl = `https://discord.com/api/oauth2/authorize?client_id=${clientId}&redirect_uri=${encodeURIComponent(redirectUri)}&response_type=code&scope=${encodeURIComponent(scope)}`;

    return (
        <a href={discordAuthUrl}>
            <button className="btn btn-secondary btn-lg">
                Connect avec Discord
            </button>
        </a>
    );
};

export default DiscordButton;
